import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState(null);
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [error, setError] = useState('');

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = async () => {
    try {
      // Validate JSON format
      const json = JSON.parse(input);

      // Call the API with JSON payload
      const res = await axios.post('http://localhost:3002/bfhl', json);
      setResponse(res.data);
      setError('');
    } catch (error) {
      console.error("Error:", error.response ? error.response.data : error.message);
      setError('Invalid JSON or API error.');
      setResponse(null);
    }
  };

  const handleFilterChange = (event) => {
    const { options } = event.target;
    const selectedOptions = Array.from(options)
      .filter(option => option.selected)
      .map(option => option.value);
    setSelectedFilters(selectedOptions);
  };

  const renderResponse = () => {
    if (!response) return null;

    // Filter response data based on selected filters
    const data = {
      numbers: selectedFilters.includes('numbers') ? response.numbers : [],
      alphabets: selectedFilters.includes('alphabets') ? response.alphabets : [],
      highest_alphabet: selectedFilters.includes('highest_alphabet') ? response.highest_alphabet : [],
    };

    return <pre>{JSON.stringify(data, null, 2)}</pre>;
  };

  return (
    <div>
      <h1>Your Roll Number</h1>
      <textarea
        rows="5"
        cols="50"
        value={input}
        onChange={handleInputChange}
        placeholder='Enter JSON here...'
      />
      <button onClick={handleSubmit}>Submit</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {response && (
        <div>
          <label>
            Filter:
            <select
              multiple
              value={selectedFilters}
              onChange={handleFilterChange}
            >
              <option value="numbers">Numbers</option>
              <option value="alphabets">Alphabets</option>
              <option value="highest_alphabet">Highest Alphabet</option>
            </select>
          </label>
          {renderResponse()}
        </div>
      )}
    </div>
  );
};

export default App;
