import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState(null);
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [error, setError] = useState('');

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = async () => {
    try {
      const json = JSON.parse(input);
      const res = await axios.post('http://localhost:3002/bfhl', json);
      setResponse(res.data);
      setError('');
    } catch (error) {
      console.error("Error:", error.response ? error.response.data : error.message);
      setError('Invalid JSON or API error.');
      setResponse(null);
    }
  };

  const handleFilterChange = (event) => {
    const { options } = event.target;
    const selectedOptions = Array.from(options)
      .filter(option => option.selected)
      .map(option => option.value);
    setSelectedFilters(selectedOptions);
  };

  const handleRemoveFilter = (filterToRemove) => {
    setSelectedFilters(selectedFilters.filter(filter => filter !== filterToRemove));
  };

  const renderResponse = () => {
    if (!response) return null;

    const filteredResponse = {
      Numbers: selectedFilters.includes('numbers') ? response.numbers.join(',') : '',
      Alphabets: selectedFilters.includes('alphabets') ? response.alphabets.join(',') : '',
      'Highest Alphabet': selectedFilters.includes('highest_alphabet') ? response.highest_alphabet[0] : '',
    };

    return (
      <>
        {Object.entries(filteredResponse).map(([key, value]) => (
          value && <p key={key}>{key}: {value}</p>
        ))}
      </>
    );
  };

  return (
    <div className="app-container">
      <h1>API Input</h1>
      <div className="input-section">
        <textarea
          rows="5"
          value={input}
          onChange={handleInputChange}
          placeholder='Enter JSON here...'
        />
        <button className="submit-button" onClick={handleSubmit}>Submit</button>
      </div>
      {error && <p className="error-message">{error}</p>}
      {response && (
        <div className="filter-section">
          <label className="filter-label">
            Multi Filter:
            <div className="filter-container">
              <select
                className="multi-select"
                multiple
                value={selectedFilters}
                onChange={handleFilterChange}
              >
                <option value="numbers">Numbers</option>
                <option value="alphabets">Alphabets</option>
                <option value="highest_alphabet">Highest Alphabet</option>
              </select>
              <span className="filter-arrow">▼</span>
            </div>
          </label>
          <div className="selected-filters">
            {selectedFilters.map(filter => (
              <span key={filter} className="filter-tag">
                {filter.replace('_', ' ')}
                <button onClick={() => handleRemoveFilter(filter)}>×</button>
              </span>
            ))}
          </div>
          <div className="filtered-response">
            {renderResponse()}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
